<template>
  <ol class="list">
    <li 
      class="item" 
      :class="`is-${index}`"
      v-for="(value, index) in getList()"
      :key="index"
    >
      {{ value }}
    </li>
  </ol>
</template>

<script lang="ts">
import { Component, Prop, Vue } from 'vue-property-decorator'
@Component
export default class OrderedList extends Vue {
  @Prop()
  private txt!: string

  private getList(): string[] {
    return this.txt
      .split(/\r?\n/)
      .map(s => s.trim().replace(/^[0-9０-９](\.?)(\s|　)/, ''))
      .filter(s => !!s)
  }
}
